import numpy as npdef summarize_frame(frame: np.ndarray) -> dict:    """    Compute summary statistics for a 2D image frame.    """    vals = frame.astype(float).ravel()    return {        "n_pixels": int(vals.size),        "min": float(np.min(vals)),        "max": float(np.max(vals)),        "mean": float(np.mean(vals)),        "median": float(np.median(vals)),        "sd": float(np.std(vals, ddof=1)),        "p10": float(np.percentile(vals, 10)),        "p90": float(np.percentile(vals, 90)),    }