from pathlib import Pathimport tifffile as tiffimport numpy as npdef load_multiframe_tiff(path: str | Path) -> np.ndarray:    """    Load a multi-frame TIFF and validate structure.    Expected shape:        (n_frames, height, width)    Returns:        numpy array    """    path = Path(path)    if not path.exists():        raise FileNotFoundError(f"File not found: {path}")    stack = tiff.imread(str(path))    if stack.ndim != 3:        raise ValueError(            f"Expected TIFF with shape (frames, H, W). "            f"Got shape {stack.shape}"        )    return stack